dc.leafletChoroplethChart = function (parent, chartGroup) {
    "use strict";
    var _chart = dc.colorChart(dc.leafletChart({}));

    var _geojsonLayer = false;
    var _dataMap = [];
    var _info = false;

    var _geojson = false;
    var _renderPopup = true;
    var _brushOn = true;
    var _featureOptions = {
        'fillColor': 'black',
        'color': 'gray',
        'opacity':0,
        'fillOpacity': 0,
        'weight': 1
    };
    var _defaultFillOpacity = 0.6;

    var _featureKey = function (feature) {
        return feature.key;
    };
    
    function isSelectedGeo(d) {
        return _chart.hasFilter(d.key);
    }    

    var _featureStyle = function (feature) {
        var options = _chart.featureOptions();
        if (options instanceof Function) {
            options = options(feature);
        }
        options = JSON.parse(JSON.stringify(options));
        var v = _dataMap[_chart.featureKeyAccessor()(feature)];
        if (v && v.d) {
            if (_chart.hasFilter()) {
                if (isSelectedGeo(v.d)) {
                    options.fillColor = _chart.getColor(v.d.value, v.i);
                    options.opacity = 0.8;
                    options.fillOpacity = _defaultFillOpacity;
                } else {
                    options.fillColor = _chart.getColor(0, v.i);
                    options.opacity = 0.8;
                    options.fillOpacity = _defaultFillOpacity;
                }
            } else {
                options.fillColor = _chart.getColor(v.d.value, v.i);
                options.opacity = 0.8;
                options.fillOpacity = _defaultFillOpacity;
            }           
        }
        return options;
    };

    var _hoverFeatureStyle = function(feature){
        var result = _featureStyle(feature);
        result.fillOpacity = 1;
        return result;
    };

    var _popup = function (d, feature) {
        return _chart.title()(d);
    };

    _chart._postRender = function () {
        _geojsonLayer = L.geoJson(_chart.geojson(), {
            style: _chart.featureStyle(),
            onEachFeature: processFeatures
        });

        _info = L.control();

        _info.onAdd = function (map) {
            this._div = L.DomUtil.create('div', 'hdx-3w-info');
            this.update();
            return this._div;
        };

        _info.update = function (name) {
            this._div.innerHTML = (name ? name: 'Hover for name');
        };

        _info.addTo(_chart.map());

        _chart.map().addLayer(_geojsonLayer);
    };

    _chart._doRedraw = function () {
        _geojsonLayer.clearLayers();
        _dataMap = [];
        _chart._computeOrderedGroups(_chart.data()).forEach(function (d, i) {
            _dataMap[_chart.keyAccessor()(d)] = {'d': d, 'i': i};
        });
        _geojsonLayer.addData(_chart.geojson());
    };

    _chart.geojson = function (_) {
        if (!arguments.length) {
            return _geojson;
        }
        _geojson = _;
        return _chart;
    };

    _chart.featureOptions = function (_) {
        if (!arguments.length) {
            return _featureOptions;
        }
        _featureOptions = _;
        return _chart;
    };

    _chart.featureKeyAccessor = function (_) {
        if (!arguments.length) {
            return _featureKey;
        }
        _featureKey = _;
        return _chart;
    };

    _chart.featureStyle = function (_) {
        if (!arguments.length) {
            return _featureStyle;
        }
        _featureStyle = _;
        return _chart;
    };

    _chart.popup = function (_) {
        if (!arguments.length) {
            return _popup;
        }
        _popup = _;
        return _chart;
    };

    _chart.renderPopup = function (_) {
        if (!arguments.length) {
            return _renderPopup;
        }
        _renderPopup = _;
        return _chart;
    };

    _chart.brushOn = function (_) {
        if (!arguments.length) {
            return _brushOn;
        }
        _brushOn = _;
        return _chart;
    };

    var processFeatures = function (feature, layer) {
        var v = _dataMap[_chart.featureKeyAccessor()(feature)];
        if (v && v.d) {
            layer.key = v.d.key;
            if (_chart.renderPopup()) {
                //layer.bindPopup(_chart.popup()(v.d, feature));
                layer.on("mouseover",function(){
                    layer.setStyle(_hoverFeatureStyle(feature));
                    _info.update(_chart.popup()(v.d, feature));
                });
                layer.on("mouseout",function(){
                    layer.setStyle(_featureStyle(feature));
                    _info.update();
                });
            }
            if (_chart.brushOn()) {
                layer.on("click", selectFilter);
            }
        }
    };

    var selectFilter = function (e) {
        if (!e.target) {
            return;
        }
        var filter = e.target.key;
        dc.events.trigger(function () {
            _chart.filter(filter);
            dc.redrawAll(_chart.chartGroup());
        });
    };

    return _chart.anchor(parent, chartGroup);
};

//function to generate the 3W component
//data is the whole 3W Excel data set
//geom is geojson file

function generate3WComponent(config,data,geom){

    var lookup = genLookup(geom,config);

    $('#title').html(config.title);
    $('#description').html(config.description);

    var containerIdPrefix = "#hdx-3W-";
    var whoContainerId = containerIdPrefix + 'who';
    var whatContainerId = containerIdPrefix + 'what';
    var whereContainerId = containerIdPrefix + 'where';

    var whoChart = dc.rowChart(whoContainerId);
    var whatChart = dc.rowChart(whatContainerId);
    var whereChart = dc.leafletChoroplethChart(whereContainerId);

    var cf = crossfilter(data);

    var whoDimension = cf.dimension(function(d){ return d[config.whoFieldName]; });
    var whatDimension = cf.dimension(function(d){ return d[config.whatFieldName]; });
    var whereDimension = cf.dimension(function(d){ return d[config.whereFieldName]; });

    var whoGroup = whoDimension.group();
    var whatGroup = whatDimension.group();
    var whereGroup = whereDimension.group();
    var all = cf.groupAll();

    var whoWidth = $(whoContainerId).width();
    var whatWidth = $(whatContainerId).width();
    var whereWidth = $(whereContainerId).width();

    whoChart.width(whoWidth).height(400)
            .dimension(whoDimension)
            .group(whoGroup)
            .elasticX(true)
            .data(function(group) {
                return group.top(15);
            })
            .labelOffsetY(13)
            .colors([config.colors[4]])
            .colorAccessor(function(d, i){return 0;})
            .xAxis().ticks(5);

    whatChart.width(whatWidth).height(400)
            .dimension(whatDimension)
            .group(whatGroup)
            .elasticX(true)
            .data(function(group) {
                return group.top(15);
            })
            .labelOffsetY(13)
            .colors([config.colors[4]])
            .colorAccessor(function(d, i){return 0;})
            .xAxis().ticks(5);

    dc.dataCount('#count-info')
            .dimension(cf)
            .group(all);

    whereChart.width(whereWidth).height(360)
            .dimension(whereDimension)
            .group(whereGroup)
            .center([0,0])
            .zoom(0)
            .geojson(geom)
            .colors(['#CCCCCC', config.colors[4]])
            .colorDomain([0, 1])
            .colorAccessor(function (d) {
                if(d>0){
                    return 1;
                } else {
                    return 0;
                }
            })
            .featureKeyAccessor(function(feature){
                return feature.properties[config.joinAttribute];
            })
            .popup(function(d){
                return lookup[d.key];
            })
            .renderPopup(true);

    dc.renderAll();

    var map = whereChart.map();
    zoomToGeom(geom);

    var g = d3.selectAll(whoContainerId).select('svg').append('g');
    
    g.append('text')
        .attr('class', 'x-axis-label')
        .attr('text-anchor', 'middle')
        .attr('x', whoWidth/2)
        .attr('y', 400)
        .text('Activities');

    var g = d3.selectAll(whatContainerId).select('svg').append('g');
    
    g.append('text')
        .attr('class', 'x-axis-label')
        .attr('text-anchor', 'middle')
        .attr('x', whatWidth/2)
        .attr('y', 400)
        .text('Activities');  


    function zoomToGeom(geom){
        var bounds = d3.geo.bounds(geom);
        map.fitBounds([[bounds[0][1],bounds[0][0]],[bounds[1][1],bounds[1][0]]]);
    }

    function genLookup(geojson,config){
        var lookup = {};
        geojson.features.forEach(function(e){
            lookup[e.properties[config.joinAttribute]] = String(e.properties[config.nameAttribute]);
        });
        return lookup;
    }
}

$(document).ready(
    function(){
        //load config
        var config = JSON.parse($('#visualization-data').val());

        //load 3W data

        var dataCall = $.ajax({
            type: 'GET',
            url: config.data,
            dataType: 'json',
        });

        //load geometry

        var geomCall = $.ajax({
            type: 'GET',
            url: config.geo,
            dataType: 'json',
        });

        //when both ready construct 3W
        $.when(dataCall, geomCall).then(function(dataArgs, geomArgs){
            if(config.datatype=='datastore'){
                dataArgs[0] = dataArgs[0]['result']['records']
            }
            if(config.geotype=='datastore'){
                geomArgs[0] = geomArgs[0]['result']['records']
            }
            var geom = geomArgs[0];
            geom.features.forEach(function(e){
                e.properties[config.joinAttribute] = String(e.properties[config.joinAttribute]);
            });
            generate3WComponent(config,dataArgs[0],geom);
        });
    }
);


